from airflow.models import DAG
from airflow.operators.python import PythonOperator
from airflow.hooks.postgres_hook import PostgresHook
from datetime import datetime

def get_trajectory_data():
    sql_stmt = "SELECT * FROM trajectory"
    pg_hook = PostgresHook(
        postgres_conn_id='airflow',
        schema='traffic_db'
    )
    pg_conn = pg_hook.get_conn()
    cursor = pg_conn.cursor()
    cursor.execute(sql_stmt)
    return cursor.fetchall()

def get_track_data():
    sql_stmt = "SELECT * FROM track"
    pg_hook = PostgresHook(
        postgres_conn_id='airflow',
        schema='traffic_db'
    )
    pg_conn = pg_hook.get_conn()
    cursor = pg_conn.cursor()
    cursor.execute(sql_stmt)
    return cursor.fetchall()

with DAG(
    dag_id='test_dag',
    schedule_interval='@daily',
    start_date=datetime(year=2024, month=5, day=3),
    catchup=False
) as dag:
    # 1. Get the track data from the track table in Postgres
    task_get_track_data = PythonOperator(
        task_id='get_track_data',
        python_callable=get_track_data,
        do_xcom_push=True
    )

    # 2. Get the trajectory data from the trajectory table in Postgres
    task_get_trajectory_data = PythonOperator(
        task_id='get_trajectory_data',
        python_callable=get_trajectory_data,
        do_xcom_push=True
    )

